import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person.js';

const App = prop =>{

  const [ personsState, setPersonsState] = useState ({
    persons : [
      {name : 'abhi', age: 19},
      {name : 'asdasd', age: 20},
      {name : 'abhi3', age: 19},
    ],
    showPerson : false
  });

  const switchHandler = () =>{
    // console.log("Was onClick");
    setPersonsState({
      persons : [
      {name : 'abhi', age: 19},
      {name : 'kumar', age: 543},
      {name : 'kjhsfk', age: 19},
    ]
    });

  }

  const newNameHandler = (event) =>{
    console.log(event.target);
    setPersonsState({
      persons : [
        {name : event.target.value, age: 19},
        {name : "hi", age: 20},
        {name : 'abhi3', age: 19},
      ]
    })
  }

  const togglePersonHandler = () =>{
    console.log("Was onClick");
    const doesShow = personsState.showPerson;
    console.log(doesShow);
    setPersonsState({showPerson : !doesShow});  
  }

  const style={
    backgroundColor: 'white',
    font:'inerit',
    padding:'10px'
  }

    return (
      <div className="App">
        <h1>Hii this is react app</h1>
        <button onClick={togglePersonHandler} style={style} >Switch it</button>

        {togglePersonHandler.showPerson === true ?
          <div>
            <Person 
              name={personsState.persons[0].name} 
              age={personsState.persons[0].age} 
              changed={newNameHandler} />
            <Person 
              name={personsState.persons[1].name} 
              age={personsState.persons[1].age} />
            <Person 
              name={personsState.persons[2].name} 
              age={personsState.persons[2].age} /> 
          </div> : null
          } 

      </div>
    );

  // return React.createElement('div',{className:'App'}, React.createElement('h1',null, 'Hi, i\'m abhi!!!'));
}

export default App;
